import * as React from 'react';
import { useState, useEffect } from 'react';

// Component
import { Header } from '../components/layout/Header';
import { FrontLines } from '../components/functions/FrontLines';
import { Explore } from '../components/containers/Explore';
import { NewFooter } from '../components/layout/NewFooter';
import { MenuModal } from '../components/modals/MenuModal';
import { Notice } from '../components/functions/Notice';
import { BottomNav } from '../components/layout/BottomNav';

// // Next
// import { NextPage } from 'next';

const Home = () => {
  const [menuModal, setMenuModal] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  });

  const handleScroll = (e) => {
    const bottom =
      e.target.scrollHeight - e.target.scrollTop === e.target.clientHeight;
    console.log('page', document.documentElement.scrollHeight);
    console.log('bottom', document.documentElement.scrollTop);
    if (bottom) {
      console.log('bottom');
    }
  };

  return (
    <>
      <div>
        <Header switchMenuModal={() => setMenuModal(!menuModal)} />
        {menuModal ? (
          <>
            <MenuModal switchMenuModal={() => setMenuModal(!menuModal)} />
          </>
        ) : null}
        <FrontLines />
        {/* COVID-19 notice */}
        <Notice />
        <Explore />
        <NewFooter />
        <BottomNav />
      </div>
    </>
  );
};

export default Home;
