import * as React from 'react';
import Link from 'next/link';

interface Prop {
  id: string;
  img: string;
  country: string;
  title: string;
  cost: number;
  period: number;
}

export const AdventureCard: React.FC<Prop> = ({
  id,
  img,
  country,
  title,
  cost,
  period
}) => {
  return (
    <Link href='/adventures/[id]' as={`/adventures/${id}`}>
      <div className='w-30/31 cursor-pointer'>
        <div>
          <img className='w-full h-full rounded-lg' src={img} />
        </div>
        <div className='mt-2 mb-1 md:mb-2'>
          <p id='card_category' className='uppercase text-2xs md:text-xs'>
            {country}
          </p>
        </div>
        <div
          className='text-sm md:text-base'
          style={{ fontFamily: 'airbnb-book' }}>
          <div className='my-1'>
            <p className='leading-4'>{title}</p>
          </div>
          <div>
            <p>
              From ${cost} / person
              <span> Â· </span>
              {period} days
            </p>
          </div>
        </div>
      </div>
    </Link>
  );
};
