export default ({ img, verified, description }) => {
  return (
    <div className='w-30/31'>
      <div className='h-full w-32 sm:w-full flex flex-wrap items-center justify-start shadow-md rounded-xl bg-white'>
        <img className='rounded-xl' src={img} alt='adventure1' />
        <div className='flex flex-wrap items-center justify-between'>
          <p className='uppercase text-xs my-2 font-bold text-pink-700'>
            {verified}+ Verified Stays
          </p>
        </div>
        <p className='flex flex-wrap bg-blue-500 my-1 text-sm tracking-wide'>
          {description}
        </p>
      </div>
    </div>
  );
};
