import * as React from 'react';

interface Props {
  title: string;
  description: string;
  order?: string | undefined;
}

export const KeepInMindCard: React.FC<Props> = ({
  title,
  description,
  order
}) => {
  return (
    <>
      {order === 'top' ? (
        <div className='w-full flex pb-4 border-b border-gray-300'>
          <div className='w-full md:w-1/3'>
            <h3
              style={{ fontFamily: 'airbnb-medium' }}
              className='text-lg md:text-2xl'>
              {title}
            </h3>
          </div>
          <div style={{ marginTop: 1 }} className='h-6 w-6 md:hidden'>
            <svg
              className='h-full w-full'
              xmlns='http://www.w3.org/2000/svg'
              viewBox='0 0 492.004 492.004'>
              <g>
                <g>
                  <path
                    d='M382.678,226.804L163.73,7.86C158.666,2.792,151.906,0,144.698,0s-13.968,2.792-19.032,7.86l-16.124,16.12
			c-10.492,10.504-10.492,27.576,0,38.064L293.398,245.9l-184.06,184.06c-5.064,5.068-7.86,11.824-7.86,19.028
			c0,7.212,2.796,13.968,7.86,19.04l16.124,16.116c5.068,5.068,11.824,7.86,19.032,7.86s13.968-2.792,19.032-7.86L382.678,265
			c5.076-5.084,7.864-11.872,7.848-19.088C390.542,238.668,387.754,231.884,382.678,226.804z'
                  />
                </g>
              </g>
            </svg>
          </div>
          <div className='w-2/3'>
            <p
              className='hidden md:block'
              style={{ fontFamily: 'airbnb-book' }}>
              {description}
            </p>
            <button
              style={{ fontFamily: 'airbnb-medium' }}
              className='hidden md:block text-green-850 text-sm my-3'>
              Learn more
            </button>
          </div>
        </div>
      ) : (
        <>
          {order === 'bottom' ? (
            <div className='w-full flex pb-4 pt-6'>
              <div className='w-1/3'>
                <h3
                  style={{ fontFamily: 'airbnb-medium' }}
                  className='text-lg md:text-2xl'>
                  {title}
                </h3>
              </div>
              <div className='w-2/3'>
                <p
                  className='hidden md:block'
                  style={{ fontFamily: 'airbnb-book' }}>
                  {description}
                </p>
                <button
                  style={{ fontFamily: 'airbnb-medium' }}
                  className='hidden md:block text-green-850 text-sm my-3'>
                  Learn more
                </button>
              </div>
            </div>
          ) : (
            <div className='w-full flex pb-4 pt-6 border-b border-gray-300'>
              <div className='w-1/3'>
                <h3
                  style={{ fontFamily: 'airbnb-medium' }}
                  className='text-lg mg:text-2xl'>
                  {title}
                </h3>
              </div>
              <div className='w-2/3'>
                <p
                  className='hidden md:block'
                  style={{ fontFamily: 'airbnb-book' }}>
                  {description}
                </p>
                <button
                  style={{ fontFamily: 'airbnb-medium' }}
                  className='hidden md:block text-green-850 text-sm my-3'>
                  Learn more
                </button>
              </div>
            </div>
          )}
        </>
      )}
    </>
  );
};
