import { useRouter } from 'next/router';
import React from 'react';
import ExploreHeader from '../../components/layout/ExploreHeader';
import { useQuery } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';

// Image
import img1 from '../../../public/img/high/airbnb-background.jpg';

const staydata = gql`
  query Stay($id: ID!) {
    stay(where: { id: $id }) {
      id
      title
    }
  }
`;

export default () => {
  const router = useRouter();
  const { loading, error, data } = useQuery(staydata, {
    variables: {
      id: router.query.id
    }
  });

  if (loading) return 'loading...';
  if (error) return 'error...';
  return (
    <>
      <ExploreHeader />
      <div className='w-screen flex flex-wrap justify-start items-start'>
        <div
          style={{ backgroundImage: `url(${img1})` }}
          className='border border-gray-800 w-1/2 h-2/3 relative bg-cover bg-no-repeat bg-blue'
        />
        <div className='w-1/2 h-2/3 relative bg-blue-500'>
          <div
            style={{ backgroundImage: `url(${img1})` }}
            className='border border-gray-800 h-1/2 relative bg-fit bg-no-repeat bg-blue'
          />
          <div
            style={{ backgroundImage: `url(${img1})` }}
            className='border border-gray-800 h-1/2 relative bg-cover bg-no-repeat bg-blue'
          />
        </div>
      </div>
    </>
  );
};
