import React, { useState, useEffect } from 'react';
import { useQuery } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';
import PulseLoader from 'react-spinners/PulseLoader';
import axios from 'axios';
import Media from 'react-media';

// Component
import AdventureCard from '../presentational/AdventureCard';
import ShowAll from '../ShowAll';

const GET_ADVENTURES = gql`
  query {
    adventures {
      id
      title
      period
      cost
      country
      img
      imglow
    }
  }
`;

export default () => {
  const { loading, error, data } = useQuery(GET_ADVENTURES);

  const [isLoading, setIsLoading] = useState(true);
  const [photos, setPhotos] = useState({});

  const info = {
    query: 'adventure',
    count: 4,
    orientation: 'portrait',
    client_id: 'RchVxgkvTlsApnvD7fdLAxFzqAa0yi6OPLS3pTWs3W4'
  };

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    axios
      .get(
        `https://api.unsplash.com/photos/random?h=140&query=${info.query}&count=${info.count}&orientation=${info.orientation}&client_id=${info.client_id}`
      )
      .then((data) => {
        setPhotos({ imgs: data.data });
      });
    setIsLoading(false);
  };

  const renderContent = (data, number) => {
    var content = [];
    for (let i = 0; i < number; i++) {
      content.push(
        <div className='2xl:w-1/6 xl:w-1/5 lg:w-1/4 md:w-1/3 w-1/2 pb-5'>
          <AdventureCard
            key={data[i].id}
            id={data[i].id}
            img={data[i].img}
            imglow={data[i].imglow}
            country={data[i].country}
            title={data[i].title}
            cost={data[i].cost}
            period={data[i].period}
          />
        </div>
      );
    }
    return content;
  };

  if (loading) {
    return (
      <div className='flex justify-center items-center w-full py-20'>
        <PulseLoader size={10} color={'#008489'} />
      </div>
    );
  }

  if (error) return `Error! ${error.message}`;

  return (
    <>
      <div className='flex items-start justify-start flex-wrap w-full'>
        {photos && data ? (
          <Media
            queries={{
              xs: '(min-width: 340px)',
              sm: '(min-width: 640px) and (max-width: 767px)',
              md: '(min-width: 768px) and (max-width: 1023px)',
              lg: '(min-width: 1024px) and (max-width: 1279px)',
              xl: '(min-width: 1280px) and (max-width: 1439px)',
              twoxl: '(min-width: 1440px) and (max-width: 1639px)',
              threexl: '(min-width: 1640px)'
            }}>
            {(matches) => (
              <>
                {matches.xs && renderContent(data.adventures, 4)}
                {matches.sm && renderContent(data.adventures, 4)}
                {matches.md && renderContent(data.adventures, 3)}
                {matches.lg && renderContent(data.adventures, 4)}
                {matches.xl && renderContent(data.adventures, 5)}
                {matches.twoxl && renderContent(data.adventures, 6)}
                {matches.threexl && renderContent(data.adventures, 6)}
              </>
            )}
          </Media>
        ) : null}

        {/* {data.adventures.map(({ id, country, title, cost, period }, index) => {
          return (

              {photos.imgs ? (
                <AdventureCard
                  key={id}
                  id={id}
                  img={photos.imgs[index].urls.full}
                  imglow={photos.imgs[index].urls.thumb}
                  country={country}
                  title={title}
                  cost={cost}
                  period={period}
                />
              ) : null}
            </div>
          );
        })} */}
      </div>

      <ShowAll title='Show all adventures' />
    </>
  );
};
