import * as React from 'react';

const adults = () => {
  return (
    <>
      <div>
        <h3>Adults</h3>
      </div>
    </>
  );
};

const infants = () => {
  return (
    <div>
      <div>
        <h3>Chldren</h3>
      </div>
      <div>
        <p>Ages 2-12</p>
      </div>
    </div>
  );
};

const children = () => {
  return (
    <div>
      <div>
        <h3>Infants</h3>
      </div>
      <div>
        <p>Under 2</p>
      </div>
    </div>
  );
};

const renderCategory = (category) => {
  switch (category) {
    case 'adults':
      return adults;
    case 'infants':
      return infants;
    case 'children':
      return children;
  }
};

const GuestPickerSection = (category) => {
  return (
    <div className='py-4'>
      <div className='flex justify-between'>
        <div>{renderCategory(category)}</div>
        <div className='flex'>
          <div className='border rounded-full border-green-500'>-</div>
          <div>0 +</div>
          <div>+ here</div>
        </div>
      </div>
    </div>
  );
};

export const GuestPickerModal = () => {
  return (
    <div className='absolute z-50 bg-white w-full px-6 shadow-xl'>
      <div className='py-8'>
        <GuestPickerSection category='Adults' />
        <GuestPickerSection category='Children' />
        <GuestPickerSection category='Infants' />
      </div>
    </div>
  );
};
