import * as React from 'react';
import { useState } from 'react';
import _ from 'lodash';

export const CurrencyModal = (setCurrencyModal) => {
  const [selectedCountry, setSelectedCountry] = useState({
    location: 'Canada',
    full: 'Canadian dollar',
    abbreviation: 'CAD',
    symbol: '$'
  });

  const currencies = [
    {
      location: 'Canada',
      full: 'Canadian dollar',
      abbreviation: 'CAD',
      symbol: '$'
    },
    {
      location: 'Australia',
      full: 'Australian dollar',
      abbreviation: 'AUD',
      symbol: '$'
    },
    {
      location: 'Brazil',
      full: 'Brazilian real',
      abbreviation: 'BRL',
      symbol: 'R$'
    },
    {
      location: 'Bulgaria',
      full: 'Bulgarian lev',
      abbreviation: 'BGN',
      symbol: 'лв'
    },
    {
      location: 'Chile',
      full: 'Chilean peso',
      abbreviation: 'CLP',
      symbol: '$'
    },
    {
      location: 'China',
      full: 'CNY',
      abbreviation: 'CNY',
      symbol: '￥'
    },
    {
      location: 'Colombia',
      full: 'Colombian peso',
      abbreviation: 'COP',
      symbol: '$'
    },
    {
      location: 'Costa Rican',
      full: 'Costa Rican colon',
      abbreviation: 'CRC',
      symbol: '₡'
    },
    {
      location: 'Croatia',
      full: 'Croatian kuna',
      abbreviation: 'HRK',
      symbol: 'kn'
    },
    {
      location: 'Czech',
      full: 'Czech koruna',
      abbreviation: 'CZK',
      symbol: 'Kč'
    },
    {
      location: 'Denmark',
      full: 'Danish krone',
      abbreviation: 'DKK',
      symbol: 'kr'
    },
    {
      location: 'United Arab Emirates',
      full: 'Emirati dirham',
      abbreviation: 'AED',
      symbol: 'د.إ'
    },
    {
      location: 'European Union',
      full: 'Euro',
      abbreviation: 'EUR',
      symbol: '€'
    },
    {
      location: 'Hong Kong',
      full: 'Hong Kong dollar',
      abbreviation: 'HKD',
      symbol: '$'
    },
    {
      location: 'Hungary',
      full: 'Fungarian forint',
      abbreviation: 'HUF',
      symbol: 'Ft'
    },
    {
      location: 'India',
      full: 'India rupee',
      abbreviation: 'INR',
      symbol: '₹'
    },
    {
      location: 'Israel',
      full: 'Israeli new shekel',
      abbreviation: 'ILS',
      symbol: '₪'
    }
  ];

  const filteredCurrencies = currencies.filter(
    (currency) => !_.isEqual(currency, selectedCountry)
  );

  console.log('filtered currencies', filteredCurrencies);
  return (
    <>
      {' '}
      <div
        id='darkOverlay'
        className='fixed w-full h-full top-0 left-0 z-20 overflow-hidden'
      />
      <div
        id='centerAbsolute'
        className='relative rounded-xl hidden fixed bg-white md:block w-248 pb-8 z-50'>
        <div className='w-full mx-auto max-w-5xl border-b border-gray-300 mt-2 flex justify-center items-center'>
          <div className='bg-green-850 relative w-full px-4 flex justify-center items-center my-6'>
            <div className='w-full flex items-center absolute left-0 z-20'>
              <button onClick={() => setCurrencyModal(false)}>
                <div className='inline-block z-10 hover:bg-gray-200 bg-white rounded-full p-2'>
                  <svg
                    className='w-4 h-4'
                    xmlns='http://www.w3.org/2000/svg'
                    viewBox='0 0 47.971 47.971'>
                    <g>
                      <path
                        d='M28.228,23.986L47.092,5.122c1.172-1.171,1.172-3.071,0-4.242c-1.172-1.172-3.07-1.172-4.242,0L23.986,19.744L5.121,0.88
		c-1.172-1.172-3.07-1.172-4.242,0c-1.172,1.171-1.172,3.071,0,4.242l18.865,18.864L0.879,42.85c-1.172,1.171-1.172,3.071,0,4.242
		C1.465,47.677,2.233,47.97,3,47.97s1.535-0.293,2.121-0.879l18.865-18.864L42.85,47.091c0.586,0.586,1.354,0.879,2.121,0.879
		s1.535-0.293,2.121-0.879c1.172-1.171,1.172-3.071,0-4.242L28.228,23.986z'
                      />
                    </g>
                  </svg>
                </div>
              </button>
            </div>
          </div>
        </div>
        <div className='w-full mx-auto max-w-5xl'>
          <div
            style={{ fontFamily: 'airbnb-medium' }}
            className='w-full text-2xl py-6 text-gray-750'>
            Choose currency
          </div>
          <div className='w-full flex flex-wrap'>
            <div className='w-1/5 flex'>
              <button className='w-90p border border-gray-750 rounded-lg flex justify-center'>
                <div className='w-80p py-2'>
                  <div className='flex'>
                    <p
                      style={{ fontFamily: 'airbnb-book' }}
                      className='text-sm'>
                      {selectedCountry.full}
                    </p>
                  </div>
                  <div className='flex'>
                    <p
                      style={{ fontFamily: 'airbnb-book' }}
                      className='text-sm text-gray-650'>
                      {selectedCountry.abbreviation} - {selectedCountry.symbol}
                    </p>
                  </div>
                </div>
              </button>
            </div>
            {filteredCurrencies.map((currency, index) => {
              return (
                <div className='w-1/5 flex'>
                  <button className='w-90p rounded-lg flex justify-center'>
                    <div className='w-80p py-2'>
                      <div className='flex'>
                        <p
                          style={{ fontFamily: 'airbnb-book' }}
                          className='text-sm'>
                          {currency.full}
                        </p>
                      </div>
                      <div className='flex'>
                        <p
                          style={{ fontFamily: 'airbnb-book' }}
                          className='text-sm text-gray-650'>
                          {currency.abbreviation} - {currency.symbol}
                        </p>
                      </div>
                    </div>
                  </button>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </>
  );
};
