import * as React from 'react';
import { useState } from 'react';
import PulseLoader from 'react-spinners/PulseLoader';

// Components
import { Location } from '../functions/Location';

// Wrapper
import { Section } from '../wrapper/Section';
import cuid from 'cuid';

interface Location {
  location: string;
  price: number;
}

export const Popular: React.FC<{}> = () => {
  const [loading, setLoading] = useState<boolean>(true);

  function sleep(ms: number) {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  const setSleep = async (seconds: number) => {
    await sleep(seconds);
    setLoading(false);
  };

  const locations: Location[] = [
    { location: 'San Francisco', price: 216 },
    {
      location: 'Los Angeles',
      price: 213
    },
    {
      location: 'New York',
      price: 159
    },
    {
      location: 'Seattle',
      price: 134
    },
    {
      location: 'Denver',
      price: 128
    },
    {
      location: 'Washington DC',
      price: 166
    },
    {
      location: 'Phoenix',
      price: 232
    },
    {
      location: 'Austin',
      price: 242
    },
    { location: 'Houston', price: 351 },
    {
      location: 'New Orleans',
      price: 210
    }
  ];

  setSleep(5000);

  return (
    <>
      <Section title='Popular destinations in the United States'>
        {loading ? (
          <div className='flex justify-center items-center w-full py-20'>
            <PulseLoader size={10} color={'#008489'} />
          </div>
        ) : (
          <div className='grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 flex flex-wrap items-center'>
            {locations.map(({ location, price }, index) => {
              return (
                <React.Fragment key={cuid()}>
                  <div className='text-gray-750'>
                    <Location key={index} location={location} price={price} />
                  </div>
                </React.Fragment>
              );
            })}
          </div>
        )}
      </Section>
    </>
  );
};
