import { useRouter } from 'next/router';
import React, { useState, useEffect } from 'react';
import { useQuery } from '@apollo/react-hooks';
import Head from 'next/head';
import Unsplash, { toJson } from 'unsplash-js';

export default () => {
  const router = useRouter();
  const [isLoading, setIsLoading] = useState(false);
  const [photos, setPhotos] = useState([]);
  const unsplash = new Unsplash({
    accessKey: 'RchVxgkvTlsApnvD7fdLAxFzqAa0yi6OPLS3pTWs3W4',
    secret: 'itaZc8B-qpxEh4q-OPI3jBP_SGQnRPB_DTEGzli26Lc'
  });

  useEffect(() => {
    unsplash.search
      .photos('home', 1, 10, { orientation: 'portrait' })
      .then(toJson)
      .then((json) => {
        setIsLoading(true);
        let photoTempArray = [];
        for (let i = 0; i < 10; i++) {
          photoTempArray.push(json.results[i].links.self);
          console.log(json.results[i].download);
        }
        setPhotos(photoTempArray);
        setIsLoading(false);
      });
  }, []);
  return (
    <>
      {/* <Head>
        <title>Vacation Rentals, Homes, Experiences & Places</title>

        <meta
          name='viewport'
          content='initial-scale=1.0, width=device-width '
        />
        <link rel='icon' type='image/x-icon' href='/img/high/favicon.ico' />
      </Head> */}
      <h1>Hello {router.query.id}</h1>
      {isLoading ? (
        <>
          {photos.map((photo, index) => {
            return (
              <div className='h-10 w-10'>
                {console.log(index, photo)}
                <img className='h-4 w-4' key={index} src={photo} alt='' />
              </div>
            );
          })}
        </>
      ) : null}
    </>
  );
};
