import React from 'react';
import Skeleton from 'react-loading-skeleton';

export default ({ loading }) => {
  return (
    <>
      <div className='z-50 fixed bottom-0 bg-white w-full lg:block lg:w-25/31 lg:sticky lg:top-0 flex lg:justify-center lg:mt-5 lg:ml-10'>
        <div className='w-full lg:flex-col flex pr-3'>
          {loading ? (
            <div className='flex justify-start mb-4'>
              <Skeleton width={300} height={30} />
            </div>
          ) : (
            <h3
              style={{ fontFamily: 'airbnb-medium' }}
              className='w-full py-5 text-2xl text-gray-850'>
              Add dates for prices
            </h3>
          )}

          <button
            style={{ fontFamily: 'airbnb-medium' }}
            className='px-4 lg:px-0 lg:w-full bg-pink-850 text-white rounded-lg py-3'>
            Check Availability
          </button>
        </div>
      </div>
    </>
  );
};
