import * as React from 'react';

import { useState } from 'react';
import Link from 'next/link';
import { RegisterModal } from '../../modals/RegisterModal';
import { HelpModal } from '../../modals/HelpModal';
import { CurrencyModal } from '../../modals/CurrencyModal';
import { LanguageModal } from '../../modals/LanguageModal';
import { OptionModal } from '../../modals/OptionModal';

interface Props {
  switchMenuModal: () => void;
}

interface CurrencyObject {
  name: string;
  sign: string;
}

export const ExperiencesHeader: React.FC<Props> = ({ switchMenuModal }) => {
  const [languageModal, setLanguageModal] = useState(false);
  const [registerModal, setRegisterModal] = useState(false);
  const [helpModal, setHelpModal] = useState(false);
  const [optionModal, setOptionModal] = useState(false);
  const [currencyModal, setCurrencyModal] = useState(false);
  const [currency, setCurrency] = useState({ sign: '$', name: 'CAD' });
  const [type, setType] = useState('');

  const switchLanguageModal = () => {
    setLanguageModal(!languageModal);
  };

  const switchRegisterModal = () => {
    setRegisterModal(!registerModal);
  };

  const switchHelpModal = () => {
    setHelpModal(!helpModal);
  };

  const switchOptionModal = () => {
    setOptionModal(!optionModal);
  };

  const switchCurrencyModal = () => {
    setCurrencyModal(!currencyModal);
  };

  const switchType = (type: string) => {
    setType(type);
  };

  const switchCurrency = (currency: CurrencyObject) => {
    setCurrency(currency);
  };

  const configureScroll = (name: string) => {
    document.body.style.overflow = name;
  };
  return (
    <>
      <div className='z-100 bg-white flex flex-wrap items-center justify-between'>
        <div className='hidden lg:block h-8 w-8 ml-6 mt-2 flex items-center'>
          <a href='/'>
            <svg
              viewBox='0 0 1000 1000'
              role='presentation'
              aria-hidden='true'
              focusable='false'
