import * as React from 'react';
import { useState, useEffect, useRef } from 'react';

import { TweenLite } from 'gsap';

// Component
import { Header } from '../components/layout/Header';
import { FrontLines } from '../components/functions/FrontLines';
import { Explore } from '../components/containers/Explore';
import { NewFooter } from '../components/layout/NewFooter';
import { MenuModal } from '../components/modals/MenuModal';
import { Notice } from '../components/functions/Notice';
import { BottomNav } from '../components/layout/BottomNav';

// // Next
// import { NextPage } from 'next';

const Home = () => {
  const [menuModal, setMenuModal] = useState(false);
  const [bottom, setBottom] = useState(false);

  var navRef = useRef(null);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    TweenLite.from(navRef, 2, { y: -50 });
    return () => window.removeEventListener('scroll', handleScroll);
  });

  const handleScroll = (e) => {
    const bottom =
      document.documentElement.scrollHeight -
        document.documentElement.scrollTop ===
      document.documentElement.clientHeight;
    if (bottom) {
      setBottom(false);
    } else {
      setBottom(true);
    }
  };

  return (
    <>
      <div>
        <Header switchMenuModal={() => setMenuModal(!menuModal)} />
        {menuModal ? (
          <>
            <MenuModal switchMenuModal={() => setMenuModal(!menuModal)} />
          </>
        ) : null}
        <FrontLines />
        {/* COVID-19 notice */}
        <Notice />
        <Explore />
        <NewFooter />
        {bottom ? (
          <div
            ref={(el) => (navRef = el)}
            className='fixed bottom-0 z-50 w-full'>
            <BottomNav />
          </div>
        ) : null}
      </div>
    </>
  );
};

export default Home;
