import styled from 'styled-components';

export const renderSkeletonVertical = (number, withLine) => {
  var content = [];

  for (let i = 0; i < number; i++) {
    content.push(
      <div className='w-full mr-3 mb-3 rounded mb-16 h-88'>
        <div className='skeleton-pulse--vertical h-full' />
        {withLine ? (
          <>
            <div className='w-full mb-3 h-4'>
              <div className='skeleton-pulse--vertical' />
            </div>
            <div className='w-80p h-4'>
              <div className='skeleton-pulse--vertical' />
            </div>
          </>
        ) : null}
      </div>
    );
  }
  return content;
};

export const renderSkeletonHorizontal = (number, withLine) => {
  var content = [];

  for (let i = 0; i < number; i++) {
    content.push(
      <div className='w-full mr-3 mb-3 rounded mb-16 h-80'>
        <div className='skeleton-pulse--horizontal' />
        {withLine ? (
          <>
            <div className='w-full mb-3 h-4'>
              <div className='skeleton-pulse--horizontal' />
            </div>
            <div className='w-80p h-4'>
              <div className='skeleton-pulse--horizontal' />
            </div>
          </>
        ) : null}
      </div>
    );
  }
  return content;
};
