import * as React from 'react';
// import { useState } from 'react';
import { useQuery } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';
import PulseLoader from 'react-spinners/PulseLoader';
import withSizes from 'react-sizes';

// Components
// import { LocationExperienceCard } from '../functions/LocationExperienceCard';
import { ShowAll } from '../ShowAll';

// Wrapper
import { Section } from '../wrapper/Section';

const GET_LOCATION_EXPERIENCES = gql`
  query LocationExperiences($available: String, $location: String) {
    experiences(
      where: { available: $available, location: $location }
      first: 6
    ) {
      id
      title
      cost
      ratings
      reviews
      location
      img
      available
      category
    }
  }
`;

const mapSizesToProps = ({ width }, { mobileBreakpoint }) => ({
  isMobile: width < mobileBreakpoint,
  isTablet: width > mobileBreakpoint && width < tabletBreakpoint,
  isLaptop: width > tabletBreakpoint && width < laptopBreakpoint,
  isDesktop: width > laptopBreakpoint
});

// interface Experience {
//   id: string;
//   title: string;
//   cost: number;
//   ratings: number;
//   reviews: number;
//   category: string;
//   img: string;
// }

// interface ExperienceData {
//   experiences: Experience[];
// }

const Today = ({ isMobile }) => {
  const { loading, error, data } = useQuery(GET_LOCATION_EXPERIENCES, {
    variables: {
      available: 'Today',
      location: 'Vancouver'
    }
  });

  if (error) return `Error! ${error.message}`;

  return (
    <>
      <Section
        title='Today in Vancouver'
        phrase='Book activities led by local hosts on your next trip.'>
        <div className='flex items-start justify-start flex-wrap w-full'>
          {loading ? (
            <div className='flex justify-center items-center w-full py-20'>
              <PulseLoader size={10} color={'#008489'} />
            </div>
          ) : (
            data && (
              <>
                <div>{isMobile ? 'Is Mobile' : 'Is Not Mobile'}</div>
                <div>{isTablet ? 'Is Tablet' : 'Is not Tablet'}</div>
                <div>{isLaptop ? 'Is Laptop' : 'Is not Laptop'}</div>
                <div>{isDesktop ? 'Is Desktop' : 'Is not Desktop'}</div>
                {/* {data.experiences.map((experience, index) => {
                  if (index == 4) {
                  }
                  return (
                    <div className='w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/6 pb-5'>
                      <LocationExperienceCard
                        key={index}
                        id={experience.id}
                        img={experience.img}
                        title={experience.title}
                        cost={experience.cost}
                        ratings={experience.ratings}
                        reviews={experience.reviews}
                        category={experience.category}
                      />
                    </div>
                  );
                })} */}
              </>
            )
          )}
        </div>
        <ShowAll title='Show all experiences' />
      </Section>
    </>
  );
};

export default withSizes(mapSizesToProps)(Today);
