import * as React from 'react';
// import { useState } from 'react';
// import { RenderSkeletonHorizontal } from '../../util/RenderSkeleton';
import styled from 'styled-components';

// Components
import { PlusCard } from '../functions/PlusCard';

// Wrapper
import { Section } from '../wrapper/Section';

// Images
// High Resolution
const img1 = require('../../../public/img/high/plus-1.jpg');

export const Plus: React.FC<{}> = () => {
  // const [loading, setLoading] = useState(true);

  // function sleep(ms: number) {
  //   return new Promise((resolve) => setTimeout(resolve, ms));
  // }

  // const setSleep = async (seconds: number) => {
  //   await sleep(seconds);
  //   setLoading(false);
  // };

  // setSleep(3000);

  const loading = true;

  return (
    <>
      <Section
        title='Airbnb Plus places to stay'
        phrase='A selection of places to stay verified for quality and design'>
        {loading ? (
          <div className='grid grid-cols-1 w-full h-88'>
            <div className='w-full mr-3 mb-3 rounded mb-16 h-full'>
              <SSkeletonPulseHorizontal />
            </div>
          </div>
        ) : (
          <PlusCard img={img1} />
        )}
      </Section>
    </>
  );
};

const SSkeletonPulse = styled.div`
  display: inline-block;
  height: 100%;
  width: 100%;
  background: linear-gradient(-90deg, #f0f0f0 0%, #f8f8f8 50%, #f0f0f0 100%);
  background-size: 400% 400%;
  animation: pulse 1.2s ease-in-out infinite;
  @keyframes pulse {
    0% {
      background-position: 0% 0%;
    }
    100% {
      background-position: -135% 0%;
    }
  }
`;

// const SSkeletonPulseVertical = styled(SSkeletonPulse)`
//   border-radius: 0.3rem;
// `;

const SSkeletonPulseHorizontal = styled(SSkeletonPulse)`
  border-radius: 0.8rem;
`;
