import React from 'react';

export default ({ description, space, access, note }) => {
  const [display, setDisplay] = useState(false);
  const renderDescription = (description) => {
    const wordArray = description.split(' ');
    if (wordArray.length < 50) {
      return <p className='text-gray-750'>description</p>;
    } else {
      const wordArray = description.split(' ');
      const newArray = [];
      for (let i = 0; i < 50; i++) {
        newArray.push(wordArray[i]);
      }
      return (
        <>
          <p style={{ fontFamily: 'airbnb-book' }} className='text-gray-750'>
            {`${newArray.join(' ')}...`}
            <button
              style={{ fontFamily: 'airbnb-book' }}
              className='hover:border-green-850 border-b text-green-850 border-transparent'
              href='/#'>
              Read More
            </button>
          </p>
        </>
      );
    }
  };
  renderDescription(description);
  return (
    <>
      {display ? (
        <div className=''>
          <p style={{ fontFamily: 'airbnb-book' }} className='text-gray-750'>
            {description}
          </p>

          <div className='py-4'>
            <h3 style={{ fontFamily: 'airbnb-medium' }} className='mb-3'>
              The space
            </h3>
            <p style={{ fontFamily: 'airbnb-book' }} className='text-gray-750'>
              {space}
            </p>
          </div>

          <div className='py-4'>
            <h3 style={{ fontFamily: 'airbnb-medium' }} className='mb-3'>
              Guest access
            </h3>
            <p style={{ fontFamily: 'airbnb-book' }} className='text-gray-750'>
              {access}
            </p>
          </div>

          <div className='py-4'>
            <h3 style={{ fontFamily: 'airbnb-medium' }} className='mb-3'>
              Other things to note
            </h3>
            <p style={{ fontFamily: 'airbnb-book' }} className='text-gray-750'>
              {note}
            </p>
          </div>
        </div>
      ) : null}
    </>
  );
};
