import { useRouter } from 'next/router';
import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';

// Components
import ExploreHeader from '../../components/layout/ExploreHeader';
import ImagePanel from '../../components/cards/ImagePanel';

// Images
import img1 from '../../../public/img/high/airbnb-background.jpg';

const staydata = gql`
  query Stay($id: ID!) {
    stay(where: { id: $id }) {
      id
      title
    }
  }
`;

export default () => {
  const router = useRouter();
  const { loading, error, data } = useQuery(staydata, {
    variables: {
      id: router.query.id
    }
  });

  if (loading) return 'loading...';
  if (error) return 'error...';
  return (
    <>
      <ExploreHeader />
      <div className='flex flex-wrap justify-start items-start'>
        <div
          style={{ backgroundImage: `url(${img1})` }}
          className='border border-gray-800 w-1/2 h-2/3 relative bg-cover bg-no-repeat bg-blue'
        />
        <div className='w-1/2 h-2/3 relative'>
          <div className='w-full h-full md:flex items-start justify-start flex-wrap'>
            <div
              style={{ backgroundImage: `url(${img1})` }}
              className='w-full border border-gray-800 md:w-1/2 h-1/2 relative bg-cover bg-no-repeat'
            />
            <div
              style={{ backgroundImage: `url(${img1})` }}
              className='w-full border border-gray-800 md:w-1/2 h-1/2 relative bg-cover bg-no-repeat'
            />
            <div
              style={{ backgroundImage: `url(${img1})` }}
              className='hidden md:block border border-gray-800 w-1/2 h-1/2 relative bg-cover bg-no-repeat'
            />
            <div
              style={{ backgroundImage: `url(${img1})` }}
              className='hidden md:block border border-gray-800 w-1/2 h-1/2 relative bg-cover bg-no-repeat'
            />
          </div>
        </div>
      </div>
    </>
  );
};
