import React, { useState, useEffect } from 'react';
import { useQuery } from '@apollo/react-hooks';
import Head from 'next/head';

export default () => {
  const router = useRouter();
  const [isLoading, setIsLoading] = useState(true);
  const [photo, setPhoto] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  // const API_URL = 'https://source.unsplash.com/random/1500×1200/?home';
  const API_URL = 'https://api.unsplash.com/photos/random';

  const loadData = async () => {
    const response = await fetch(API_URL);
    console.log(response);
    // setPhoto(response.url);
    // setIsLoading(false);
  };

  return (
    <>
      <Head>
        <title>Vacation Rentals, Homes, Experiences & Places</title>

        <meta
          name='viewport'
          content='initial-scale=1.0, width=device-width '
        />
        <link rel='icon' type='image/x-icon' href='/img/high/favicon.ico' />
      </Head>
      <h1>Hello {router.query.id}</h1>
      {isLoading ? (
        <h1>Loading</h1>
      ) : (
        <div className=''>
          <img src={photo} alt='' />
        </div>
      )}
    </>
  );
};
