import * as React from 'react';

export const ThreeColumnCard = ({ img, category, title, description }) => {
  return (
    <div className=''>
      <div className='sm:w-full'>
        <div>
          <img
            className='w-full h-full rounded-xl'
            src={img}
            alt='adventure1'
          />
        </div>
        <div>
          <p
            style={{ fontFamily: 'airbnb-medium' }}
            className='uppercase text-gray-650 text-xs'>
            {category}
          </p>
        </div>
        <div className='flex flex-wrap items-center justify-start'>
          <p className='uppercase text-xs my-2 font-bold text-pink-700'>
            {title}
          </p>
        </div>
        <div>
          <p className='whitespace-normal my-1 text-sm tracking-wide'>
            {description}
          </p>
        </div>
      </div>
    </div>
  );
};
