import * as React from 'react';

const renderCategory = (
  category,
  adult,
  incrementAdult,
  decrementAdult,
  children,
  incrementChildren,
  decrementChildren,
  infant,
  incrementInfant,
  decrementInfant
) => {
  switch (category) {
    case 'Adults':
      return (
        <>
          <div>
            <div className='text-gray-750'>
              <h3 style={{ fontFamily: 'airbnb-bold' }}>Adults</h3>
            </div>
          </div>
          <div className='flex items-center'>
            <button
              onClick={(e) => {
                e.preventDefault();
                decrementAdult();
              }}>
              <div className='w-7 h-7'>
                <svg
                  className='w-full h-full'
                  viewBox='0 0 512 512'
                  style={{ fill: '#008489' }}
                  xmlns='http://www.w3.org/2000/svg'>
                  <path d='m256 512c-141.164062 0-256-114.835938-256-256s114.835938-256 256-256 256 114.835938 256 256-114.835938 256-256 256zm0-480c-123.519531 0-224 100.480469-224 224s100.480469 224 224 224 224-100.480469 224-224-100.480469-224-224-224zm0 0' />
                  <path d='m368 272h-224c-8.832031 0-16-7.167969-16-16s7.167969-16 16-16h224c8.832031 0 16 7.167969 16 16s-7.167969 16-16 16zm0 0' />
                </svg>
              </div>
            </button>
            <div className='px-3'>
              <p
                style={{ fontFamily: 'airbnb-medium' }}
                className='text-lg text-gray-750'>
                {adult}+
              </p>
            </div>
            <button
              onClick={(e) => {
                e.preventDefault();
                incrementAdult();
              }}>
              <div className='h-7 w-7'>
                <svg
                  style={{ fill: '#008489' }}
                  className='w-full h-full'
                  viewBox='0 0 512 512'
                  xmlns='http://www.w3.org/2000/svg'>
                  <path d='m256 512c-141.164062 0-256-114.835938-256-256s114.835938-256 256-256 256 114.835938 256 256-114.835938 256-256 256zm0-480c-123.519531 0-224 100.480469-224 224s100.480469 224 224 224 224-100.480469 224-224-100.480469-224-224-224zm0 0' />
                  <path d='m368 272h-224c-8.832031 0-16-7.167969-16-16s7.167969-16 16-16h224c8.832031 0 16 7.167969 16 16s-7.167969 16-16 16zm0 0' />
                  <path d='m256 384c-8.832031 0-16-7.167969-16-16v-224c0-8.832031 7.167969-16 16-16s16 7.167969 16 16v224c0 8.832031-7.167969 16-16 16zm0 0' />
                </svg>
              </div>
            </button>
          </div>
        </>
      );
    case 'Children':
      return (
        <>
          <div>
            <div className='text-gray-750'>
              <div>
                <h3 style={{ fontFamily: 'airbnb-bold' }}>Children</h3>
              </div>
              <div>
                <p style={{ fontFamily: 'airbnb-book' }} className='text-sm'>
                  Ages 2 - 12
                </p>
              </div>
            </div>
          </div>
          <div className='flex items-center'>
            <button
              onClick={(e) => {
                e.preventDefault();
                decrementChildren();
              }}>
              <div className='w-7 h-7'>
                <svg
                  className='w-full h-full'
                  viewBox='0 0 512 512'
                  style={{ fill: '#008489' }}
                  xmlns='http://www.w3.org/2000/svg'>
                  <path d='m256 512c-141.164062 0-256-114.835938-256-256s114.835938-256 256-256 256 114.835938 256 256-114.835938 256-256 256zm0-480c-123.519531 0-224 100.480469-224 224s100.480469 224 224 224 224-100.480469 224-224-100.480469-224-224-224zm0 0' />
                  <path d='m368 272h-224c-8.832031 0-16-7.167969-16-16s7.167969-16 16-16h224c8.832031 0 16 7.167969 16 16s-7.167969 16-16 16zm0 0' />
                </svg>
              </div>
            </button>
            <div className='px-3'>
              <p
                style={{ fontFamily: 'airbnb-medium' }}
                className='text-lg text-gray-750'>
                {children}+
              </p>
            </div>
            <button
              onClick={(e) => {
                e.preventDefault();
                incrementChildren();
              }}>
              <div className='h-7 w-7'>
                <svg
                  style={{ fill: '#008489' }}
                  className='w-full h-full'
                  viewBox='0 0 512 512'
                  xmlns='http://www.w3.org/2000/svg'>
                  <path d='m256 512c-141.164062 0-256-114.835938-256-256s114.835938-256 256-256 256 114.835938 256 256-114.835938 256-256 256zm0-480c-123.519531 0-224 100.480469-224 224s100.480469 224 224 224 224-100.480469 224-224-100.480469-224-224-224zm0 0' />
                  <path d='m368 272h-224c-8.832031 0-16-7.167969-16-16s7.167969-16 16-16h224c8.832031 0 16 7.167969 16 16s-7.167969 16-16 16zm0 0' />
                  <path d='m256 384c-8.832031 0-16-7.167969-16-16v-224c0-8.832031 7.167969-16 16-16s16 7.167969 16 16v224c0 8.832031-7.167969 16-16 16zm0 0' />
                </svg>
              </div>
            </button>
          </div>
        </>
      );
    case 'Infants':
      return (
        <>
          <div>
            <div className='text-gray-750'>
              <div>
                <h3 style={{ fontFamily: 'airbnb-bold' }}>Infants</h3>
              </div>
              <div>
