import { useRouter } from 'next/router';
import React, { useState, useEffect } from 'react';
import { useQuery } from '@apollo/react-hooks';
import Head from 'next/head';
import axios from 'axios';

export default () => {
  const router = useRouter();
  const [isLoading, setIsLoading] = useState(true);
  const [photo, setPhotos] = useState({});

  useEffect(() => {
    loadData('home');
  }, []);

  // const API_URL = 'https://source.unsplash.com/random/1500×1200/?home';
  const API_URL =
    'https://api.unsplash.com/photos/random/?query=home&client_id=RchVxgkvTlsApnvD7fdLAxFzqAa0yi6OPLS3pTWs3W4';

  const loadData = async (query) => {
    axios
      .get(
        `https://api.unsplash.com/search/photos/?page=1&per_page=10&query=${query}&client_id=RchVxgkvTlsApnvD7fdLAxFzqAa0yi6OPLS3pTWs3W4`
      )
      .then((data) => {
        setPhotos({ imgs: data.data.results });
      });
  };
