import React, { useState } from 'react';
import PulseLoader from 'react-spinners/PulseLoader';

// Components
import { ThreeColumnCard } from '../functions/ThreeColumnCard';

// Wrapper
import { SectionOverflow } from '../wrapper/SectionOverflow';
import cuid from 'cuid';

export const ThreeColumn = ({ sectionTitle, sectionDescription }) => {
  const [loading, setLoading] = useState(true);

  const locationData = [
    {
      img: featured1,
      category: 'Stays + Experiences',
      title: 'Classic NYC',
      description:
        'The quintessential American city, NYC is hip-hop’s birthplace and the home of Broadway — explore the cultural capital with Airbnb.'
    },
    {
      img: featured1,
      category: 'Stays + Experiences',
      title: 'Classic NYC',
      description:
        'The quintessential American city, NYC is hip-hop’s birthplace and the home of Broadway — explore the cultural capital with Airbnb.'
    },
    {
      img: featured1,
      category: 'Stays + Experiences',
      title: 'Classic NYC',
      description:
        'The quintessential American city, NYC is hip-hop’s birthplace and the home of Broadway — explore the cultural capital with Airbnb.'
    }
  ];

  return (
    <>
      <SectionOverflow title={sectionTitle} phrase={sectionDescription}>
        {loading ? (
          <div className='flex justify-center items-center w-full py-20'>
            <PulseLoader size={10} color={'#008489'} />
          </div>
        ) : (
          <div className='overflow-y-hidden'>
            <div className='w-full h-full overflow-y-hidden'>
              <div className='h-full scroller'>
                <div className='scrollable sm:inset-x-0 flex items-start justify-start py-2 rounded-xl w-featured md:w-full'>
                  {locationData.map((location) => {
                    return (
                      <React.Fragment key={cuid()}>
                        <div className='w-80 lg:w-1/3 pb-5 mr-2'>
                          <ThreeColumnCard {...location} />
                        </div>
                      </React.Fragment>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        )}
      </SectionOverflow>
    </>
  );
};
